var index = 0

var personImg = document.getElementById("img")
var personName = document.getElementById("name")
var personProfession = document.getElementById("proffesion")
var personInfo = document.getElementById("about")

var next = document.getElementById("next")
var prev = document.getElementById("prev")

function showInfo(i) {
  var r = peopleInformation[i]
  console.log(r)
  personImg.src = r.img
  personName.innerHTML = r.name
  personProfession.innerHTML = r.proffesion
  personInfo.innerHTML = r.about
}


next.onclick = function () {
  index++
index %= peopleInformation.length
  showInfo(index)
};


prev.onclick = function () {
  index--
  index = (index + peopleInformation.length) % peopleInformation.length;
  showInfo(index)
}



showInfo(index)
